(function(a){a.fn.badger=function(b,e){var c=this.find("#Badger").html();if(!b){if(c){this.find("#Badger").remove()}}else{var d=this.find("#Badge").text();if(b.charAt(0)=="+"){if(isNaN(b.substr(1))){b=d+b.substr(1)}else{b=Math.round(Number(d)+Number(b.substr(1)))}}else{if(b.charAt(0)=="-"){if(isNaN(b.substr(1))){b=d-b.substr(1)}else{b=Math.round(Number(d)-Number(b.substr(1)))}}}if(c){this.find("#Badge").html(b)}else{this.append('<div class="badger-outter" id="Badger"><div class="badger-inner"><p class="badger-badge" id="Badge">'+b+"</p></div></div>")}if(isNaN(b)){this.find("#Badge").removeClass("badger-number").addClass("badger-text")}else{this.find("#Badge").removeClass("badger-text").addClass("badger-number")}if(e){e(b)}}}})(jQuery);